import numpy as np
def calculate_mean_per_index(data_dict):
    # Convert dictionary values to a NumPy array
    values = np.array(list(data_dict.values()))
    
    # Calculate the mean along the 0th axis (across keys)
    means = np.mean(values, axis=0)
    
    print(means)
def get_lists_along_axes(data_dict):
    # Convert dictionary values to a list of lists
    print('New')
    values = list(data_dict.values())
    
    # Transpose the list of lists to get the values along each axis
    axes_lists = list(map(list, zip(*values)))
    
    # Format the output to only show values up to 2 decimal places
    formatted_axes_lists = [[round(value, 2) for value in axis] for axis in axes_lists]
    
    print(formatted_axes_lists)
